targets.query:
from(bucket: v.defaultBucket)
  |> range(start: -5m)
  |> filter(fn: (r) => r["_measurement"] == "${measurement}" and r.box == "${boxtag}" and
    (
        r._field == "receiverate" or
        r._field == "downstreammax"
    )
  )
  |> drop(columns: ["box", "_measurement", "_start", "_stop"])
  |> last()
  |> map(fn: (r) =>  ({
    r with _value:
      if r._field == "receiverate" then r._value * 8
      else r._value,
    })
  )

from(bucket: v.defaultBucket)
  |> range(start: -5m)
  |> filter(fn: (r) => r["_measurement"] == "${measurement}" and r.box == "${boxtag}" and
    (
        r._field == "sendrate" or
        r._field == "upstreammax"
    )
  )
  |> drop(columns: ["box", "_measurement", "_start", "_stop"])
  |> last()
  |> map(fn: (r) =>  ({
    r with _value:
      if r._field == "sendrate" then r._value * 8
      else r._value,
    })
  )

// SELECT last("ram_usage_fixed") + last("ram_usage_dynamic") FROM /^$measurement$/ WHERE ("box" =~ /^$boxtag$/) AND $timeFilter GROUP BY time($__interval) fill(linear)
from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "${measurement}" and r.box == "${boxtag}" and
    (
      r._field == "ram_usage_fixed" or r._field == "ram_usage_dynamic"
    )
  )
  |> last()
  |> keep(columns: ["_value"])

from(bucket: v.defaultBucket)
  |> range(start: -1d)
  |> filter(fn: (r) => r._measurement == "${measurement}" and r.box == "${boxtag}" and
    (
      r._field == "myfritz_host_name" or
      r._field == "vpn_type" or
      r._field == "vpn_user_num_active"
    ) 
  )
  |> last()
  |> toString()
  |> drop(columns: ["box", "_measurement", "_start", "_stop", "_time"])
  |> map(fn: (r) =>  ({
        r with _field:
          if r._field == "myfritz_host_name" then "MyFritz Hostname"
          else if r._field == "vpn_type" then "VPN Type"
          else if r._field == "vpn_user_num_active" then "VPN User Active"
          else r._field
        })
      )
